              let clone_scale_anot = _.cloneDeep(find_Recent_Annot);
              let scaleValue = clone_scale_anot["annotation_url"]['scaleValue'];
              let scalewidth = clone_scale_anot["annotation_url"]["scaleAgainst"].width
