  checkShapes(elementId, length, height) {
    console.log(elementId, length, height);
    switch (elementId) {
      case 1:
        //"circle"
        return (((Math.PI * length) / 2) * length) / 2;
      case 2:
        //                "octagon"
        return 2 * (1 + Math.sqrt(2)) * (length * length);
      case 3:
        //                "square"
        return length * length;
      case 4:
        //                "triangle"
        return (length * height) / 2;
      case 5:
        //                "star"
        return length * length;
      case 6:
        //                "diamond"
        return length * length;
      case 7:
        //"flag"
        return length * length;
      case 8:
        //                "camera"
        return length * length;
      case 9:
        //                "arrow"
        return length * length;
      case 10:
        //                "callout"
        return length * length;
      case 11:
        //                "text"
        return length * length;
      case 12:
        //                "freehand_line"
        return length * length;
      case 13:
        //                "polyline_arrow"
        return length * length;
      case 14:
        //                "polyline"
        return length * length;
      case 15:
        //                "line"
        return length * length;
      case 16:
        //                "line_axial"
        return length * length;
      case 17:
        //                "freehand_area"
        return length * length;
      case 18:
        //                "polygon"
        return length * length;
      case 19:
        //                "ellipse"
        return (((Math.PI * length) / 2) * height) / 2;
      case 20:
        //                "rectangle"
        return length * height;
    }
  }
