  calculateRectPos(startX, startY, endX, endY) {
    var width = endX - startX;
    var height = endY - startY;
    var posX = startX;
    var posY = startY;

    if (width < 0) {
      width = Math.abs(width);
      posX -= width;
    }

    if (height < 0) {
      height = Math.abs(height);
      posY -= height;
    }
    return {
      left: parseInt(posX),
      top: parseInt(posY),
      width: width,
      height: height,
    };
  }

